function Collapse ({title, text}) {
    const [ visible , setVisible ] = useState (false)
    const toggleCollapse = () => {
        setVisible(!visible)
    } 

    return (
        <div className='about_container'>
            <div className='about_content'  onClick={setVisible(!visible)}>
                <h1 className='about_title'>{title}</h1>
                <button className={`about_button_container ${visible? 'about_button_container_rotate' : ''}`} onClick={() => {toggleCollapse}}>
                        <i className={`fa-solid ${visible? 'fa-chevron-down fa-2xl' : 'fa-chevron-up fa-2xl'}`}></i>
                </button>
            </div>
            {visible&&(
                <div className='about_text_content'>
                    <p className='about_text'>{text}</p>
                </div>
            )}
        </div>
    ) }

