import React, { useState } from 'react';

function Collapse ({title, text}) {
    const [ visible, setVisible ] = useState(false)

    const toggleContent = () => {
        setVisible(!visible);
      };

    const arrowIcon = `fa-solid ${visible? 'fa-chevron-down fa-2xl' : 'fa-chevron-up fa-2xl'}`;

    return (
        <div className='about_container'>
            <div className='about_content'>
                <h1 className='about_title'>{title}</h1>
                <button onClick={toggleContent} className={`${visible? 'about_button_container_rotateDown' : 'about_button_container'}`} 
                >
                    <i className={arrowIcon}></i>
                </button>
            </div>
            {visible&&(
                <div className='about_text_content'>
                    <p className='about_text'>{text}</p>
                </div>
            )}
        </div>
    )   
}


export default Collapse;
