import React, { useState } from 'react';

function Collapse ({title, text}) {
    const [ visible , setVisible ] = useState ( false ) 
    return (
        <div className='about_container'>
            <button className='about_title_content' onClick={() => setVisible(!visible)}>
                <div className="text-icon-button">
                    <h1 className="title-button">{title}</h1>
                    <i className={`fa-solid ${visible? "fa-chevron-down" : "fa-chevron-up"}`}></i>
                </div>
            </button>
            {visible&&(
        <div className='box-text-about'>
          <p className='text-about'>{text}</p>
        </div>
      )}
        </div>
    )   


}

export default Collapse;

